import React, { useContext } from 'react';
import { LinkContainer } from 'react-router-bootstrap';
import { Navbar, Nav, NavDropdown, Container } from 'react-bootstrap';
import { CartContext } from '../context/CartContext';
import { removeInStorage, getInStorage } from '../utils/persistStorage';

const Header = (props) => {
   const { location, history } = props;
   const { state } = useContext(CartContext);
   const userExist = getInStorage('user');

   const handleLogout = () => {
      removeInStorage('user');
      window.location = '/';
   };

   return (
      <header>
         <Navbar bg='primary' variant='dark' expand='lg' collapseOnSelect>
            <Container>
               <LinkContainer to='/'>
                  <Navbar.Brand>Sneaker City</Navbar.Brand>
               </LinkContainer>
               <Navbar.Toggle aria-controls='basic-navbar-nav' />
               <Navbar.Collapse id='basic-navbar-nav'>
                  <Nav className='mr-auto'>
                     <LinkContainer to={`/${'category=women'}`}>
                        <Nav.Link>Women</Nav.Link>
                     </LinkContainer>
                     <LinkContainer to={`/${'category=mem'}`}>
                        <Nav.Link>Men</Nav.Link>
                     </LinkContainer>
                  </Nav>
                  <Nav className='ml-auto'>
                     <LinkContainer to='/cart'>
                        <Nav.Link>
                           <i className='fas fa-shopping-cart'></i>
                           {state.itemCount} Cart
                        </Nav.Link>
                     </LinkContainer>
                     {userExist ? (
                        <NavDropdown title={userExist.name} id='basic-nav-dropdown'>
                           <LinkContainer>
                              <NavDropdown.Item>Action</NavDropdown.Item>
                           </LinkContainer>
                           <NavDropdown.Item onClick={() => handleLogout()}>
                              Logout
                           </NavDropdown.Item>
                           <NavDropdown.Divider />
                        </NavDropdown>
                     ) : (
                        <LinkContainer to='/login'>
                           <Nav.Link>
                              <i className='fas fa-user'></i>
                              Sign In
                           </Nav.Link>
                        </LinkContainer>
                     )}
                  </Nav>
               </Navbar.Collapse>
            </Container>
         </Navbar>
      </header>
   );
};

export default Header;
