import React, { useState, useEffect, useContext } from 'react';
import { Link } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Card, Button } from 'react-bootstrap';
import Rating from '../components/Rating';
import axios from 'axios';
import { StoreContext } from '../context/StoreContext';
import { CartContext } from '../context/CartContext';

const ProductPage = (props) => {
   const { id } = props.match.params;
   const [product, setProduct] = useState({});
   const { fetchProductById } = useContext(StoreContext);
   const { addProduct, increase } = useContext(CartContext);

   const fetchSelectedProduct = () => {
      const selectedProduct = fetchProductById(id);
      setProduct(selectedProduct);
   };

   useEffect(fetchSelectedProduct, []);

   return (
      <>
         <Link className='btn btn-dark my-3' to='/'>
            Go Back
         </Link>
         <Row>
            <Col md={6}>
               <Image src={product.image} fluid />
            </Col>
            <Col md={3}>
               <ListGroup variant='flush'>
                  <ListGroup.Item>
                     <span className='lead'>{product.name}</span>
                  </ListGroup.Item>
                  <ListGroup.Item>
                     <span className='h4'>Brand: {product.brand}</span>
                  </ListGroup.Item>
                  <ListGroup.Item>
                     <Rating value={product.rating} text={`${product.numReviews} reviews`} />
                  </ListGroup.Item>
                  <ListGroup.Item className='h5'>Price: ${product.price}</ListGroup.Item>
                  <ListGroup.Item>
                     <span className='h5'>Description:</span>{' '}
                     <span className='font-weight-lighter'>{product.description}</span>
                  </ListGroup.Item>
               </ListGroup>
            </Col>
            <Col md={3}>
               <Card>
                  <ListGroup variant='flush'>
                     <ListGroup.Item>
                        <Row>
                           <Col>Price</Col>
                           <Col>
                              <strong>${product.price}</strong>
                           </Col>
                        </Row>
                     </ListGroup.Item>
                     <ListGroup.Item>
                        <Row>
                           <Col>Status</Col>
                           <Col>
                              <strong>
                                 {product.countInStock > 0 ? 'In Stock' : 'Out of Stock'}
                              </strong>
                           </Col>
                        </Row>
                     </ListGroup.Item>
                     <ListGroup.Item>
                        <Button
                           className='btn-block'
                           type='button'
                           disabled={product.countInStock === 0}
                        >
                           Add to Cart
                        </Button>
                     </ListGroup.Item>
                  </ListGroup>
               </Card>
            </Col>
         </Row>
      </>
   );
};

export default ProductPage;
