import React, { useState, useContext, useEffect } from 'react';
import { Form, Button } from 'react-bootstrap';
import { StoreContext } from '../context/StoreContext';
import axios from 'axios';
import FormContainer from '../components/FormContainer';
import Message from '../components/FormContainer';

export const Profile = (props) => {
   const [name, setName] = useState('');
   const [email, setEmail] = useState('');
   const [password, setPassword] = useState('');
   const [confirmPassword, setConfirmPassword] = useState('');
   const [error, setError] = useState('');

   const { existUser, updateSavedUser } = useContext(StoreContext);

   useEffect(() => {
      const fetchProfile = async () => {
         try {
            let config = {
               headers: {
                  'Authorization': `Bearer ${existUser.token}`,
                  'Content-Type': 'application/json',
               },
            };
            const response = await axios.patch('/api/users', { name, email, password }, config);
            const fecthedUser = response.data;
            setName(fecthedUser.name);
            setEmail(fecthedUser.email);
         } catch (err) {
            if (err.response.data) {
               setError(err.response.data.message);
            } else setError(err.message);
         }
      };
      fetchProfile();
   }, [existUser]);

   const handleUpdate = (e) => {
      e.preventDefault();
      try {
         const response = axios.patch('/api/users', { name, email, password });
         const updatedUser = response.data;
         updateSavedUser(updatedUser);
      } catch (err) {
         if (err.response.data) setError(err.response.data.message);
         else setError(err.message);
      }
   };

   return (
      <FormContainer>
         <Form onSubmit={handleUpdate}>
            <h3 className='my-5'>Sign In</h3>
            {error && <Message variant='danger'>{error}</Message>}
            <Form.Group controlId='name'>
               <Form.Label>Full Name</Form.Label>
               <Form.Control
                  type='text'
                  placeholder='Enter name'
                  value={name}
                  onChange={(e) => setName(e.target.value)}
               ></Form.Control>
            </Form.Group>
            <Form.Group controlId='email'>
               <Form.Label>Email Address</Form.Label>
               <Form.Control
                  type='email'
                  placeholder='Enter email'
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
               ></Form.Control>
            </Form.Group>
            <Form.Group controlId='password'>
               <Form.Label>Password</Form.Label>
               <Form.Control
                  type='password'
                  placeholder='Enter password'
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
               ></Form.Control>
            </Form.Group>
            <Form.Group controlId='confirm-password'>
               <Form.Label>Confirm Password</Form.Label>
               <Form.Control
                  type='password'
                  placeholder='Confirm password'
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
               ></Form.Control>
            </Form.Group>
            <Button type='submit' variant='primary'>
               Register
            </Button>
         </Form>
      </FormContainer>
   );
};
