import React, { useContext } from 'react';
import { CartContext } from '../context/CartContext';
import { Row, Col, ListGroup, Image, Button, Card } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import Message from '../components/Message';
import { StoreContext } from '../context/StoreContext';

export const CartPage = (props) => {
   const { state, productInCart, increase, decrease } = useContext(CartContext);
   const { cartItems, itemCount, total } = state;

   const handleIncrease = (product, size) => {
      increase({ product, size });
   };

   const handleDecrease = (product, size) => {
      decrease({ product, size });
   };

   return (
      <div>
         <Row>
            <Col md={8}>
               <h3>Shopping Cart</h3>
               <hr />
               {cartItems.length === 0 ? (
                  <Message>
                     Your cart is empty <Link to='/'>Go Back</Link>
                  </Message>
               ) : (
                  <ListGroup variant='flush'>
                     {cartItems.map((item) => (
                        <ListGroup.Item>
                           <Row>
                              <Col md={2}>
                                 <Image
                                    src={productInCart(item._id).image}
                                    alt={productInCart(item._id).name}
                                    fluid
                                    rounded
                                 />
                              </Col>
                              <Col md={2}>
                                 <Link to={`/product/${item._id}`}>
                                    {productInCart(item._id).name}
                                 </Link>
                              </Col>
                              <Col md={2}>
                                 ${item.price} x {item.quantity}
                              </Col>
                              <Col md={2}>
                                 {item.size}
                                 {'"'}
                              </Col>
                              <Col md={4}>
                                 <Button
                                    type='button'
                                    variant='light'
                                    onClick={() =>
                                       handleIncrease(productInCart(item._id), item.size)
                                    }
                                 >
                                    <i className='fas fa-plus' />
                                 </Button>
                                 <Button
                                    type='button'
                                    variant='light'
                                    onClick={() =>
                                       handleDecrease(productInCart(item._id), item.size)
                                    }
                                 >
                                    <i className='fas fa-minus' />
                                 </Button>
                                 <Button type='button' variant='light'>
                                    <i className='fas fa-trash' />
                                 </Button>
                              </Col>
                              <Col md={2}></Col>
                           </Row>
                        </ListGroup.Item>
                     ))}
                  </ListGroup>
               )}
            </Col>
            <Col md={4}>
               <Card>
                  <ListGroup variant='flush'>
                     <ListGroup.Item>
                        <h5>Subtotal </h5>
                        <h6>Items - {itemCount}</h6>
                        <h6>${total}</h6>
                     </ListGroup.Item>
                     <ListGroup.Item>
                        <Button
                           type='button'
                           className='btn-block'
                           disabled={cartItems.length === 0}
                        >
                           Checkout
                        </Button>
                     </ListGroup.Item>
                  </ListGroup>
               </Card>
            </Col>
         </Row>
      </div>
   );
};
