export const CartReducer = (state, action) => {
   switch (action.type) {
      case 'INCREASE':
         state.cartItems[state.cartItems.findIndex((product) => product._id === action.payload._id)]
            .quantity++;
         return {
            ...state,
            cartItems: [...state.cartItems],
         };
      case 'DECREASE':
         state.cartItems[state.cartItems.findIndex((product) => product._id === action.payload._id)]
            .quantity--;
         return {
            ...state,
            cartItems: [...state.cartItems],
         };
      case 'ADD_PRODUCT':
         const item = action.payload;
         return;
      case 'REMOVE_PRODUCT':
         return;
      default:
         return;
   }
};

export const sumItems = (cartItems) => {
   let itemCount = cartItems.reduce((count, product) => count + product.quantity, 0);

   let total = cartItems
      .reduce((sum, product) => sum + product.price * product.quantity, 0)
      .toFixed(2);
   return { itemCount, total };
};
