export const CartReducer = (state, action) => {
   switch (action.type) {
      case 'INCREASE':
         const { currentProduct, size, qty } = action.payload;
         const index = state.cartItems.findIndex((item) => item._id === currentProduct._id);
         state.cartItems[index].quantity++;
         return {
            ...state,
            cartItems: [...state.cartItems],
            ...sumItems(state.cartItems),
         };
      case 'DECREASE':
         state.cartItems[state.cartItems.findIndex((product) => product._id === action.payload._id)]
            .quantity--;
         return {
            ...state,
            cartItems: [...state.cartItems],
         };
      case 'ADD_PRODUCT':
         const exist = state.cartItems.find(
            (item) => item._id === action.payload.currentProduct._id
         );
         if (exist) {
            const index = state.cartItems.findIndex((item) => item._id === currentProduct._id);
            state.cartItems[index].quantity++;
         } else {
            state.cartItems.push({
               _id: action.payload.currentProduct._id,
               price: action.payload.currentProduct.price,
               size: action.payload.size,
               quantity: action.payload.qty,
            });
         }
         return {
            ...state,
            cartItems: [...state.cartItems],
            ...sumItems(state.cartItems),
         };
      case 'REMOVE_PRODUCT':
         return;
      default:
         return;
   }
};

export const sumItems = (cartItems) => {
   let itemCount = cartItems.reduce((count, item) => count + item.quantity, 0);
   let total = cartItems.reduce((sum, item) => sum + item.price * item.quantity, 0).toFixed(2);
   return { itemCount, total };
};
