import React, { useState, useContext, createContext, useReducer } from 'react';
import { CartReducer, sumItems } from './CartReducer';

export const CartContext = createContext();

const initialState = { cartItems: [], ...sumItems([]), checkout: false };

export const CartContextProvider = (props) => {
   const [state, dispatch] = useReducer(CartReducer, initialState);

   const increase = (payload) => {
      dispatch({ type: 'INCREASE', payload });
   };

   const decrease = (payload) => {
      dispatch({ type: 'DECREASE', payload });
   };

   const addProduct = (payload) => {
      dispatch({ type: 'ADD_PRODUCT', payload });
   };

   const removeProduct = (payload) => {
      dispatch({ type: 'REMOVE_PRODUCT', payload });
   };

   const getProductsInCart = () => {
      const productsInCart = state.cartItems.map((item) => {
         return products.filter((p) => p._id === item._id);
      });
      return productsInCart;
   };

   return (
      <CartContext.Provider
         value={{ increase, decrease, addProduct, removeProduct, state, getProductsInCart }}
      >
         {props.children}
      </CartContext.Provider>
   );
};
