export const CartReducer = (state, action) => {
   switch (action.type) {
      case 'INCREASE':
         state.cartItems[state.cartItems.findIndex((product) => product._id === action.payload.id)]
            .quantity++;
         return {
            ...state,
            cartItems: [...state.cartItems],
         };
      case 'DECREASE':
         state.cartItems[state.cartItems.findIndex((product) => product._id === action.payload.id)]
            .quantity--;
         return {
            ...state,
            cartItems: [...state.cartItems],
         };
      case 'ADD_PRODUCT':
         if (!state.cartItems.find((item) => item._id === action.palyload.id)) {
            state.cartItems.push({ ...action.palyload, quantity: 1 });
         }
         return {
            ...state,
            cartItems: [...state.cartItems],
         };
      case 'REMOVE_PRODUCT':
         return;
      default:
         return;
   }
};

export const sumItems = (cartItems) => {
   let itemCount = cartItems.reduce((sum, product) => sum + product.quantity, 0);

   let total = cartItems
      .reduce((sum, product) => total + product.price * product.quantity, 0)
      .toFixed(2);
   return { itemCount, total };
};
