import React from 'react';
import { Pagination } from 'react-bootstrap';
import _ from 'lodash';

const Paginator = (props) => {
   const {
      next,
      prev,
      jump,
      currentPage,
      maxPage,
      startPage,
      endPage,
      pageSize,
      displayPages,
   } = props;

   const pages = _.range(startPage, endPage + 1);
   console.log(startPage);
   console.log(endPage + 1);
   console.log(pages);
   return (
      <Pagination>
         {currentPage > 1 ? <Pagination.Prev onClick={() => prev()} /> : ''}
         {/* {currentPage > displayPages ? (
            <>
               <Pagination.Item active={maxPage === 1} onClick={() => jump(1)}>
                  {1}
               </Pagination.Item>
               <Pagination.Ellipsis />
            </>
         ) : (
            ''
         )} */}
         {pages.map((page) => (
            <Pagination.Item active={page === currentPage} key={page} onClick={() => jump(page)}>
               {page}
            </Pagination.Item>
         ))}
         {/* {currentPage + 2 <= maxPage ? (
            <>
               <Pagination.Ellipsis />
               <Pagination.Item active={maxPage === currentPage} onClick={() => jump(maxPage)}>
                  {maxPage}
               </Pagination.Item>
            </>
         ) : (
            ''
         )} */}
         {currentPage < maxPage ? <Pagination.Next onClick={() => next()} /> : ''}
      </Pagination>
   );
};

export default Paginator;
